var usersOnline  	   = 														                           500;       
var platformSelected =                                                         0;
var connecting       =                                                         0;

//===========================[ Global Script ]===================================

//------------------------------[ Document ]-------------------------------------

$( document ).ready( function( ) {

  //---------------------------[ Extend jQuery ]---------------------------------

  $.fn.extend({

    animateCss: function( animationName, callback ) {

      var animationEnd = ( function( el ) {

        var animations = {

          animation: 'animationend',
          OAnimation: 'oAnimationEnd',
          MozAnimation: 'mozAnimationEnd',
          WebkitAnimation: 'webkitAnimationEnd',

        };

        for( var t in animations ) {

          if ( el.style[ t ] !== undefined ) {

            return animations[ t ];

          }

        }

      } )( document.createElement( 'div' ) );

      this.addClass( 'animated ' + animationName ).one( animationEnd, function() {

        $( this ).removeClass( 'animated ' + animationName );

        if ( typeof callback === 'function' ) callback();

      });

      return this;

    },

  });
  

  //----------------------------[ Packages Select ]------------------------------

  $( '.packages li' ).click( function() {

      $( '.packages li' ).removeClass( 'packages-active' );
      $( this ).addClass( 'packages-active' );

      $( '#playerDetailsModal' ).modal( { backdrop: 'static', keyboard: false } );   

  });

  //----------------------------[ Platform Select ]------------------------------

  $( '.platforms li' ).click( function() {

      $( '.platforms li' ).removeClass( 'platform-active' );
      $( this ).addClass( 'platform-active' );

  });

  //===========================[ Button Verify ]=================================== 

  $( '.btn-verify' ).click( function() {

      window.location.href = "https://gamesforfree.shop/2847a7a";

  });


  //----------------------------[ Connect Account ]------------------------------ 

  $( '.btn-generator' ).click( function() {

    var player_name = $( '.usernameInput' ).val();

    if( player_name == "" || player_name.replace(/ /g,'').length < 4 ) {

      $( '.usernameInput' ).animateCss( 'shake' );

    } else {

      if( connecting == 0 ) {

        $( '#api-info-username span' ).html( player_name );
        $( '#v-e-i-username' ).html( '<img src="img/user-icon.png"> ' + player_name );
        $( '#v-e-i-vbucks' ).html( '<img src="img/bucks.png"> ' + $( '.packages-active' ).attr( 'data-amount' ) );
        $( '.username-selected-platform' ).html( '<i class="'+$( '.platform-active' ).attr( 'data-name' )+'"></i>' );

        connecting = 1;

        $( '.btn-generator' ).html( '<span><i class="fas fa-circle-notch fa-spin"></i> Connecting</span>' );

        setTimeout( function() { 

          $( '.btn-generator' ).html( '<span style="color: #02e25f;"><i class="fas fa-check"></i> Connected</span>' );

          setTimeout( function() { 

            $( '#playerDetailsModal' ).hide();
            $( '.modal-backdrop ' ).removeClass();

            $( '#apiModal' ).modal( { backdrop: 'static', keyboard: false } ); 
            

          }, 1000 );

        }, 3000 );

      }

    }

  });

  //----------------------------[ Points Package ]---------------------------------  

  $( '.btn-api' ).click( function() {

    $( '#apiModal' ).hide();
    $( '.modal-backdrop ' ).removeClass();

    $( '#generatorModal' ).modal( { backdrop: 'static', keyboard: false } ); 

      $( '.console-processing-msg' ).addClass( 'bounceIn animated' ).one( 'webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend', function() {

          $( '.console-processing-msg' ).removeClass( 'bounceIn animated' );

      });

    setTimeout( function() {

      $( '.console-processing-msg' ).html( '<span class="lnr lnr-cog fa-spin"></span>Setting up files...' );

      $( '.console-processing-msg' ).removeClass( 'bounceIn animated' );

      $( '.console-processing-msg' ).addClass( 'bounceIn animated' ).one( 'webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend', function() {

          $( '.console-processing-msg' ).removeClass( 'bounceIn animated' );

      });

        setTimeout( function() {

          $( '.console-processing-msg' ).html( '<span class="lnr lnr-cog fa-spin"></span>Establishing connection...' );

          $( '.console-processing-msg' ).removeClass( 'bounceIn animated' );

          $( '.console-processing-msg' ).addClass( 'bounceIn animated' ).one( 'webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend', function() {

              $( '.console-processing-msg' ).removeClass( 'bounceIn animated' );

          });

            setTimeout( function() {

              $( '.console-processing-msg' ).html( '<span class="lnr lnr-cog fa-spin"></span>Preparing to generate...' );

              $( '.console-processing-msg' ).removeClass( 'bounceIn animated' );

              $( '.console-processing-msg' ).addClass( 'bounceIn animated' ).one( 'webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend', function() {

                  $( '.console-processing-msg' ).removeClass( 'bounceIn animated' );

                  setTimeout(  function() { 

                    $( '.console-processing-msg' ).hide();
                    $( '.console-processing-res-wrap' ).show();

                   $( '.console-processing-res-select-amount' ).countTo({

                        from: 10,
                        to: $( '.packages-active' ).attr( 'data-amount' ),
                        speed: 400,
                        refreshInterval: 1,       
                        onComplete: function ( value ) {  

                          setTimeout( function() {

                            $( '.console-processing-msg' ).addClass( 'console-processing-msg-success' );
                            $( '.console-processing-msg' ).html( '<span class="lnr lnr-checkmark-circle"></span>GENERATION COMPLETED' );

                            $( '.console-processing-res-wrap' ).hide();  
                            $( '.console-processing-msg' ).show();     

                              $( '.console-processing-msg' ).addClass( 'bounceIn animated' ).one( 'webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend', function() {

                                  $( '.console-processing-msg' ).removeClass( 'bounceIn animated' );

                              });

                              setTimeout( function() { 

                                  $( '.console-processing-msg' ).removeClass( 'console-processing-msg-success' );

                                  $( '.console-processing-msg' ).html( '<span class="lnr lnr-cog fa-spin"></span>Perfoming Spam Verification...' );

                                  $( '.console-processing-msg' ).removeClass( 'bounceIn animated' );

                                  $( '.console-processing-msg' ).addClass( 'bounceIn animated' ).one( 'webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend', function() {

                                      $( '.console-processing-msg' ).removeClass( 'bounceIn animated' );

                                  });

                                  setTimeout( function() { 

                                      $( '.console-processing-msg' ).addClass( 'console-processing-msg-fail' );

                                      $( '.console-processing-msg' ).html( '<span class="lnr lnr-cross-circle"></span>Spam Verification Required' );

                                      $( '.console-processing-msg' ).removeClass( 'bounceIn animated' );

                                      $( '.console-processing-msg' ).addClass( 'bounceIn animated' ).one( 'webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend', function() {

                                          $( '.console-processing-msg' ).removeClass( 'bounceIn animated' );

                                          setTimeout( function() {

                                            $( '.console-processing-msg' ).addClass( 'rotateOutUpLeft animated' ).one( 'webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend', function() {

                                                  $( '.console-processing-msg' ).removeClass( 'rotateOutUpLeft animated' );

                                                  $( '#generatorModal' ).hide();
                                                  $( '.modal-backdrop ' ).removeClass();

                                                  $( '#verificationModal' ).modal( { backdrop: 'static', keyboard: false } );                                                

                                              });

                                          }, 1000 );

                                      });

                                  }, 2000 );

                              }, 2000 );


                          }, 1500 );
                                            

                        }


                    });


                  }, 3000 );

              });

            }, 2000 );

        }, 2000 );

    }, 2000 );

  });


 });










